import React, {useState} from 'react'

function Addtodo(props){

const [input, setInput] = useState('')

const handleChange = e => {
    setInput(e.target.value)
}

const handleSubmit = e => {
    e.preventDefault()

    props.onSubmit({
        id: Math.floor(Math.random() * 10000),
        text: input
    })
    setInput('')
}

    return (
        <div>
        <from className="mx-auto px-6 py-3 text-center" onSubmit={handleSubmit}>
            <input
            type="text"
            name="task"
            placeholder="Add a task"
            value={input}
            className="font-semibold shadow appearance-none border rounded py-1 px-2 text-grey-darker ml-3"
            onChange={handleChange}
            required
            />
            <button 
            className="bg-gray-500 ml-3 w-8 h-8 p-4 text-sm font-bold tracking-wider text-white rounded-full hover:bg-yellow-600 inline-flex items-center justify-center"
            >+
            </button>
        </from>
        </div>
        )
}
export default Addtodo
