import React from 'react'

const TodosList = ({todos, setTodos, setEditTodo}) => {

    const handleComplete = (todo) => {
        setTodos(
            todos.map((item) =>{
                if(item.id === todo.id) {
                    return {...item, completed: !item.completed}
                }
                return item
            })
        )
    }

    const handleEdit = ({id}) => {
        const findTodo = todos.find((todo) => todo.id === id)
        setEditTodo(findTodo)
    }

    const handleDelete = ({id}) => {
        setTodos(todos.filter((todo) => todo.id !== id))
    }

    return (
        <div>
            {todos.map((todo) => (
                <section 
                className = "list-item flex items-center justify-center bg-white"
                key = {todo.id}>
                    <input 
                    type="text"
                    value = {todo.title}
                    className = {`list ${todo.completed} ? "complete" : "" `}
                    onChange = {(e) => e.preventDefault()}
                    />
                <div>
                    <button
                    className="puppercase font-semibold tracking-wide bg-green-100 text-green-700 px-2 py-1 rounded-lg mt-2 focus:outline-none hover:bg-green-200 text-xs"
                    onClick = {() => handleComplete(todo)}
                    >Completed
                    </button>
                    <button
                    className="ml-2 puppercase font-semibold tracking-wide bg-yellow-100 text-red-700 px-2 py-1 rounded-lg mt-2 focus:outline-none hover:bg-yellow-200 text-xs"
                    onClick = {() => handleEdit(todo)}
                    >Edit
                    </button>
                    <button
                    className="ml-2 puppercase font-semibold tracking-wide bg-red-100 text-red-700 px-2 py-1 rounded-lg mt-2 focus:outline-none hover:bg-red-200 text-xs"
                    onClick = {() => handleDelete(todo)}
                    >Deleted
                    </button>
                </div>
                </section>
            )
            )}
        <div>
            <section className="flex items-center justify-center bg-white">
            <div className="max-w-lg w-full rounded-lg shadow-lg p-4">
                <h3 className="font-semibold text-xm text-gray-700 tracking-wide">d
                </h3>
                <div>
                    <button 
                    className="puppercase font-semibold tracking-wide bg-green-100 text-green-700 px-2 py-1 rounded-lg mt-2 focus:outline-none hover:bg-green-200 text-xs">
                    Done
                    </button>
                    <button 
                    className="ml-2 puppercase font-semibold tracking-wide bg-red-100 text-red-700 px-2 py-1 rounded-lg mt-2 focus:outline-none hover:bg-red-200 text-xs">
                    Remove
                    </button>
                </div>
            </div>
            </section>
        </div>
        </div>
    )
}

export default TodosList;
